mapper.update:
update reservation
set status_cd = 704
where res_id = #{resId}

update member_game
set member_id = #{memberId}
where member_name = #{memberName}
and game_id = #{gameId}

mapper.select:
SELECT CASE WHEN COUNT(MEMBER_ID) = 1 THEN 1 ELSE 0 END AS MEMBER_ID
FROM MEMBER
WHERE MEMBER_ID = #{memberId}
and password = #{password}
and member_name = #{memberName}
