mapper.select:
SELECT *
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

    AND PASSWORD = #{password}

mapper.update:
UPDATE MEMBER
SET PASSWORD = #{password},
    TEL      = #{tel}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET TEAM_ID = #{teamId}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET GRADE = #{grade}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET BLACKLIST_YN     = #{blacklistYn},
    BLACKLIST_REASON = #{blacklistReason}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET TEAM_ID = 0
WHERE MEMBER_ID = #{memberId}
