mapper.select:
SELECT SUM(PAYMENT_AMOUNT) AS SUM, COUNT(PAYMENT_AMOUNT) AS COUNT, PROD_INFO_CD, PAYMENT_METHOD_CD FROM PAYMENT WHERE TO_CHAR(PAYMENT_DATE, 'YYYY-MM-DD') BETWEEN #{firstDate} AND #{lastDate} AND PAYMENT_STATUS_CD NOT IN (801, 803) AND PAYMENT_METHOD_CD NOT IN (1703)
AND TO_CHAR(PAYMENT_DATE, 'D') = #{day}
GROUP BY PROD_INFO_CD, PAYMENT_METHOD_CD

SELECT CASE WHEN MEMBER_ID = '0' THEN '비회원' ELSE '회원' END AS "member", SUM(PAYMENT_AMOUNT) AS "paymentAmount"
FROM PAYMENT
where TO_CHAR(PAYMENT_DATE, 'YYYY-MM-DD') BETWEEN #{firstDate} AND #{lastDate}

    AND TO_CHAR(PAYMENT_DATE, 'D') = #{day}

GROUP BY CASE WHEN MEMBER_ID = '0' THEN '비회원' ELSE '회원' END

SELECT CASE WHEN PAYMENT_METHOD_CD = '1701' THEN '현장결제' WHEN PAYMENT_METHOD_CD = '1702' THEN '네이버페이' WHEN PAYMENT_METHOD_CD = '1703' THEN '카카오페이' WHEN PAYMENT_METHOD_CD = '1704' THEN '무통장입금' END AS "pay" , SUM(PAYMENT_AMOUNT) AS "paymentAmount" FROM PAYMENT where TO_CHAR(PAYMENT_DATE, 'YYYY-MM-DD') BETWEEN #{firstDate} AND #{lastDate}
AND TO_CHAR(PAYMENT_DATE, 'D') = #{day}
GROUP BY CASE WHEN PAYMENT_METHOD_CD = '1701' THEN '현장결제' WHEN PAYMENT_METHOD_CD = '1702' THEN '네이버페이' WHEN PAYMENT_METHOD_CD = '1703' THEN '카카오페이' WHEN PAYMENT_METHOD_CD = '1704' THEN '무통장입금' END
